// –°–Ω–∞—á–∞–ª–∞ —Ñ–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –∏ –≥—Ä—É–ø–ø–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ
const formattedData = {};

items.forEach(item => {
  const data = item.json;
  const campaignId = data.campaign_id || '';
  const date = data.time_1d || '';
  const uniqueKey = `${date}_${campaignId}`;

  const formattedItem = {
    unique_key: uniqueKey,
    campaign_id: campaignId,
    date: date,
    impressions: parseInt(data.impressions) || 0,
    clicks: parseInt(data.clicks) || 0,
    installs: parseInt(data.installs) || 0,
    spend: data.gross_spend ? parseFloat(data.gross_spend).toFixed(2) : '0.00',
    cpi: data.cpi && data.cpi !== '' ? parseFloat(data.cpi) : 0,
    ctr: data.cr ? parseFloat(data.cr) : 0,
    ipm: data.ipm ? parseFloat(data.ipm) : 0
  };

  // –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –ø–æ campaign_id –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞ –∞–Ω–æ–º–∞–ª–∏–π
  if (!formattedData[campaignId]) {
    formattedData[campaignId] = [];
  }
  formattedData[campaignId].push(formattedItem);
});

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞—Å—á–µ—Ç–∞ –ø—Ä–æ—Ü–µ–Ω—Ç–Ω–æ–≥–æ –∏–∑–º–µ–Ω–µ–Ω–∏—è
function calculateChange(prev, current) {
  if (prev === 0) return 0;
  return ((current - prev) / prev) * 100;
}

// –ê–Ω–∞–ª–∏–∑–∏—Ä—É–µ–º –∞–Ω–æ–º–∞–ª–∏–∏ –∏ –¥–æ–±–∞–≤–ª—è–µ–º —Ñ–ª–∞–≥–∏
const finalItems = [];

Object.keys(formattedData).forEach(campaignId => {
  const campaignData = formattedData[campaignId];

  // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ –¥–∞—Ç–µ
  campaignData.sort((a, b) => new Date(a.date) - new Date(b.date));

  // –ê–Ω–∞–ª–∏–∑–∏—Ä—É–µ–º –∫–∞–∂–¥—É—é –∑–∞–ø–∏—Å—å
  campaignData.forEach((current, index) => {
    let anomalyFlag = '';
    let anomalyDetails = '';

    if (index > 0) {
      const prev = campaignData[index - 1];

      // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –¥–Ω–∏ —Å –º–∞–ª—ã–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ–º –¥–∞–Ω–Ω—ã—Ö
      if (prev.impressions >= 100 && current.impressions >= 100) {
        const cpiChange = calculateChange(prev.cpi, current.cpi);
        const ctrChange = calculateChange(prev.ctr, current.ctr);
        const ipmChange = calculateChange(prev.ipm, current.ipm);

        const threshold = 30;
        let anomalies = [];

        if (Math.abs(cpiChange) >= threshold && prev.cpi > 0) {
          anomalies.push(`CPI ${cpiChange > 0 ? '+' : ''}${cpiChange.toFixed(1)}%`);
        }

        if (Math.abs(ctrChange) >= threshold && prev.ctr > 0) {
          anomalies.push(`CTR ${ctrChange > 0 ? '+' : ''}${ctrChange.toFixed(1)}%`);
        }

        if (Math.abs(ipmChange) >= threshold && prev.ipm > 0) {
          anomalies.push(`IPM ${ipmChange > 0 ? '+' : ''}${ipmChange.toFixed(1)}%`);
        }

        if (anomalies.length > 0) {
          anomalyFlag = anomalies.length >= 2 ? 'üî¥ HIGH' : 'üü° MEDIUM';
          anomalyDetails = anomalies.join(' | ');
        }
      }
    }

    finalItems.push({
      json: {
        unique_key: current.unique_key,
        campaign_id: current.campaign_id,
        date: current.date,
        impressions: current.impressions,
        clicks: current.clicks,
        installs: current.installs,
        spend: current.spend,
        cpi: current.cpi > 0 ? current.cpi.toFixed(2) : 'N/A',
        ctr: current.ctr.toFixed(2),
        ipm: current.ipm.toFixed(2),
        anomaly_flag: anomalyFlag,
        anomaly_details: anomalyDetails
      }
    });
  });
});

return finalItems;

// –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ –∑–∞–ø–∏—Å–∏ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ –Ω–æ–¥–∞
const allRecords = $input.all().map(item => item.json);

// ============================================
// –ö–û–ù–§–ò–ì–£–†–ê–¶–ò–Ø –ü–û–†–û–ì–û–í–´–• –ó–ù–ê–ß–ï–ù–ò–ô
// ============================================

// –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–µ –ø–æ—Ä–æ–≥–∏ –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —É—Ä–æ–≤–Ω–µ–π –≤–∞–∂–Ω–æ—Å—Ç–∏ –∫–∞–º–ø–∞–Ω–∏–π
// –ù–∞—Å—Ç—Ä–æ–µ–Ω–æ –ø–æ–¥ —Ä–µ–∞–ª—å–Ω—ã–µ –æ–±—ä–µ–º—ã Tricky –∫–∞–º–ø–∞–Ω–∏–π
const VOLUME_TIERS = {
  CRITICAL: { min_installs: 150, min_spend: 80 },   // –¢–æ–ø –∫–∞–º–ø–∞–Ω–∏–∏ (>150 –∏–Ω—Å—Ç–∞–ª–æ–≤ –∏–ª–∏ >$80)
  HIGH: { min_installs: 60, min_spend: 40 },        // –ö—Ä—É–ø–Ω—ã–µ –∫–∞–º–ø–∞–Ω–∏–∏ (60-150 –∏–Ω—Å—Ç–∞–ª–æ–≤ –∏–ª–∏ $40-80)
  MEDIUM: { min_installs: 20, min_spend: 15 },      // –°—Ä–µ–¥–Ω–∏–µ –∫–∞–º–ø–∞–Ω–∏–∏ (20-60 –∏–Ω—Å—Ç–∞–ª–æ–≤ –∏–ª–∏ $15-40)
  LOW: { min_installs: 5, min_spend: 5 }            // –ú–∞–ª—ã–µ –∫–∞–º–ø–∞–Ω–∏–∏ (5-20 –∏–Ω—Å—Ç–∞–ª–æ–≤ –∏–ª–∏ $5-15)
};

// –ü–æ—Ä–æ–≥–∏ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–π –¥–ª—è —Ä–∞–∑–Ω—ã—Ö –º–µ—Ç—Ä–∏–∫ (–≤ –ø—Ä–æ—Ü–µ–Ω—Ç–∞—Ö)
const DEVIATION_THRESHOLDS = {
  CRITICAL: { cpi: 40, ctr: 40, ipm: 40 },  // –ö—Ä–∏—Ç–∏—á–µ—Å–∫–æ–µ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–µ
  SEVERE: { cpi: 30, ctr: 30, ipm: 30 },    // –°–µ—Ä—å–µ–∑–Ω–æ–µ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–µ
  MODERATE: { cpi: 20, ctr: 20, ipm: 20 },  // –£–º–µ—Ä–µ–Ω–Ω–æ–µ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–µ
  MINOR: { cpi: 15, ctr: 15, ipm: 15 }      // –ù–µ–±–æ–ª—å—à–æ–µ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–µ
};

// ============================================
// –ì–†–£–ü–ü–ò–†–û–í–ö–ê –ò –†–ê–°–ß–ï–¢ –°–†–ï–î–ù–ò–•
// ============================================

const campaignStats = {};

allRecords.forEach(record => {
  if (!campaignStats[record.campaign_id]) {
    campaignStats[record.campaign_id] = {
      cpi_values: [],
      ctr_values: [],
      ipm_values: [],
      spend_values: [],
      installs_values: []
    };
  }

  // –°–æ–±–∏—Ä–∞–µ–º —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫—É —Ç–æ–ª—å–∫–æ –¥–ª—è –¥–Ω–µ–π —Å –¥–æ—Å—Ç–∞—Ç–æ—á–Ω—ã–º–∏ –¥–∞–Ω–Ω—ã–º–∏
  if (record.installs >= 5) {
    if (record.cpi && !isNaN(record.cpi)) {
      campaignStats[record.campaign_id].cpi_values.push(parseFloat(record.cpi));
    }
    if (record.ctr && !isNaN(record.ctr)) {
      campaignStats[record.campaign_id].ctr_values.push(parseFloat(record.ctr));
    }
    if (record.ipm && !isNaN(record.ipm)) {
      campaignStats[record.campaign_id].ipm_values.push(parseFloat(record.ipm));
    }
    campaignStats[record.campaign_id].spend_values.push(parseFloat(record.spend) || 0);
    campaignStats[record.campaign_id].installs_values.push(parseInt(record.installs) || 0);
  }
});

// –í—ã—á–∏—Å–ª—è–µ–º —Å—Ä–µ–¥–Ω–∏–µ –∑–Ω–∞—á–µ–Ω–∏—è –¥–ª—è –∫–∞–∂–¥–æ–π –∫–∞–º–ø–∞–Ω–∏–∏
const campaignAverages = {};

Object.keys(campaignStats).forEach(campaignId => {
  const stats = campaignStats[campaignId];

  campaignAverages[campaignId] = {
    avg_cpi: stats.cpi_values.length > 0 
      ? stats.cpi_values.reduce((a, b) => a + b, 0) / stats.cpi_values.length : null,
    avg_ctr: stats.ctr_values.length > 0 
      ? stats.ctr_values.reduce((a, b) => a + b, 0) / stats.ctr_values.length : null,
    avg_ipm: stats.ipm_values.length > 0 
      ? stats.ipm_values.reduce((a, b) => a + b, 0) / stats.ipm_values.length : null,
    avg_spend: stats.spend_values.length > 0 
      ? stats.spend_values.reduce((a, b) => a + b, 0) / stats.spend_values.length : null,
    avg_installs: stats.installs_values.length > 0 
      ? stats.installs_values.reduce((a, b) => a + b, 0) / stats.installs_values.length : null
  };
});

// ============================================
// –§–£–ù–ö–¶–ò–Ø –û–ü–†–ï–î–ï–õ–ï–ù–ò–Ø –£–†–û–í–ù–Ø –û–ë–™–ï–ú–ê –ö–ê–ú–ü–ê–ù–ò–ò
// ============================================

function getCampaignVolumeTier(installs, spend) {
  if (installs >= VOLUME_TIERS.CRITICAL.min_installs && spend >= VOLUME_TIERS.CRITICAL.min_spend) {
    return 'CRITICAL';
  } else if (installs >= VOLUME_TIERS.HIGH.min_installs && spend >= VOLUME_TIERS.HIGH.min_spend) {
    return 'HIGH';
  } else if (installs >= VOLUME_TIERS.MEDIUM.min_installs && spend >= VOLUME_TIERS.MEDIUM.min_spend) {
    return 'MEDIUM';
  } else if (installs >= VOLUME_TIERS.LOW.min_installs) {
    return 'LOW';
  }
  return 'IGNORE'; // –°–ª–∏—à–∫–æ–º –º–∞–ª–µ–Ω—å–∫–∞—è –∫–∞–º–ø–∞–Ω–∏—è
}

// ============================================
// –§–£–ù–ö–¶–ò–Ø –†–ê–°–ß–ï–¢–ê IMPACT SCORE
// ============================================

function calculateImpactScore(record, anomalies, volumeTier) {
  let score = 0;

  // –ë–∞–∑–æ–≤—ã–π score –Ω–∞ –æ—Å–Ω–æ–≤–µ –æ–±—ä–µ–º–∞
  const volumeScores = { CRITICAL: 100, HIGH: 60, MEDIUM: 30, LOW: 10 };
  score += volumeScores[volumeTier] || 0;

  // –î–æ–±–∞–≤–ª—è–µ–º –±–∞–ª–ª—ã –∑–∞ –∫–∞–∂–¥—É—é –∞–Ω–æ–º–∞–ª–∏—é —Å —É—á–µ—Ç–æ–º —Å–µ—Ä—å–µ–∑–Ω–æ—Å—Ç–∏ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏—è
  anomalies.forEach(anomaly => {
    const deviation = Math.abs(anomaly.deviation);

    if (deviation >= 50) {
      score += 50; // –û—á–µ–Ω—å –±–æ–ª—å—à–æ–µ –æ—Ç–∫–ª–æ–Ω–µ–Ω–∏–µ
    } else if (deviation >= 40) {
      score += 35;
    } else if (deviation >= 30) {
      score += 25;
    } else if (deviation >= 20) {
      score += 15;
    } else {
      score += 5;
    }
  });

  // –ë–æ–Ω—É—Å –∑–∞ –º–Ω–æ–∂–µ—Å—Ç–≤–µ–Ω–Ω—ã–µ –º–µ—Ç—Ä–∏–∫–∏ (—Å–∏–Ω–µ—Ä–≥–µ—Ç–∏—á–µ—Å–∫–∏–π —ç—Ñ—Ñ–µ–∫—Ç)
  if (anomalies.length >= 3) {
    score += 30;
  } else if (anomalies.length >= 2) {
    score += 15;
  }

  // –£—á–∏—Ç—ã–≤–∞–µ–º –∞–±—Å–æ–ª—é—Ç–Ω—ã–π —Ä–∞–∑–º–µ—Ä spend (—Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–æ –ø–æ–¥ —Ä–µ–∞–ª—å–Ω—ã–µ –æ–±—ä–µ–º—ã Tricky)
  const dailySpend = parseFloat(record.spend) || 0;
  if (dailySpend > 100) {
    score += 20;  // –û—á–µ–Ω—å –∫—Ä—É–ø–Ω—ã–π –¥–ª—è Tricky ($100+)
  } else if (dailySpend > 60) {
    score += 12;  // –ö—Ä—É–ø–Ω—ã–π ($60-100)
  } else if (dailySpend > 30) {
    score += 5;   // –°—Ä–µ–¥–Ω–∏–π ($30-60)
  }

  return score;
}

// ============================================
// –§–£–ù–ö–¶–ò–Ø –û–ü–†–ï–î–ï–õ–ï–ù–ò–Ø –ê–ù–û–ú–ê–õ–ò–ô –° –ì–†–ê–î–ê–¶–ò–ï–ô
// ============================================

function detectAnomalies(record, averages) {
  if (!averages) return null;

  const installs = parseInt(record.installs) || 0;
  const spend = parseFloat(record.spend) || 0;

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º tier –∫–∞–º–ø–∞–Ω–∏–∏
  const volumeTier = getCampaignVolumeTier(installs, spend);

  // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º —Å–ª–∏—à–∫–æ–º –º–∞–ª–µ–Ω—å–∫–∏–µ –∫–∞–º–ø–∞–Ω–∏–∏
  if (volumeTier === 'IGNORE') return null;

  const anomalies = [];

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º CPI (—Ä–æ—Å—Ç - –ø–ª–æ—Ö–æ)
  if (record.cpi && averages.avg_cpi && averages.avg_cpi > 0) {
    const cpiChange = (record.cpi - averages.avg_cpi) / averages.avg_cpi;
    const cpiDeviation = cpiChange * 100;

    // –ü—Ä–∏–º–µ–Ω—è–µ–º –ø–æ—Ä–æ–≥–∏ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç tier –∫–∞–º–ø–∞–Ω–∏–∏
    let threshold = DEVIATION_THRESHOLDS.MODERATE.cpi;
    if (volumeTier === 'CRITICAL') {
      threshold = DEVIATION_THRESHOLDS.MINOR.cpi; // –ë–æ–ª–µ–µ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã –∫ –∫—Ä—É–ø–Ω—ã–º –∫–∞–º–ø–∞–Ω–∏—è–º
    } else if (volumeTier === 'LOW') {
      threshold = DEVIATION_THRESHOLDS.SEVERE.cpi; // –ú–µ–Ω–µ–µ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã –∫ –º–∞–ª—ã–º
    }

    if (cpiDeviation > threshold) {
      anomalies.push({
        metric: 'CPI',
        deviation: cpiDeviation,
        current: record.cpi,
        average: averages.avg_cpi,
        direction: 'increase',
        text: `CPI +${cpiDeviation.toFixed(1)}% ($${record.cpi.toFixed(2)} vs avg $${averages.avg_cpi.toFixed(2)})`
      });
    }
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º CTR (–ø–∞–¥–µ–Ω–∏–µ - –ø–ª–æ—Ö–æ)
  if (record.ctr && averages.avg_ctr && averages.avg_ctr > 0) {
    const ctrChange = (record.ctr - averages.avg_ctr) / averages.avg_ctr;
    const ctrDeviation = ctrChange * 100;

    let threshold = DEVIATION_THRESHOLDS.MODERATE.ctr;
    if (volumeTier === 'CRITICAL') {
      threshold = DEVIATION_THRESHOLDS.MINOR.ctr;
    } else if (volumeTier === 'LOW') {
      threshold = DEVIATION_THRESHOLDS.SEVERE.ctr;
    }

    if (ctrDeviation < -threshold) {
      anomalies.push({
        metric: 'CTR',
        deviation: ctrDeviation,
        current: record.ctr,
        average: averages.avg_ctr,
        direction: 'decrease',
        text: `CTR ${ctrDeviation.toFixed(1)}% (${record.ctr.toFixed(2)}% vs avg ${averages.avg_ctr.toFixed(2)}%)`
      });
    }
  }

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º IPM (–ø–∞–¥–µ–Ω–∏–µ - –ø–ª–æ—Ö–æ)
  if (record.ipm && averages.avg_ipm && averages.avg_ipm > 0) {
    const ipmChange = (record.ipm - averages.avg_ipm) / averages.avg_ipm;
    const ipmDeviation = ipmChange * 100;

    let threshold = DEVIATION_THRESHOLDS.MODERATE.ipm;
    if (volumeTier === 'CRITICAL') {
      threshold = DEVIATION_THRESHOLDS.MINOR.ipm;
    } else if (volumeTier === 'LOW') {
      threshold = DEVIATION_THRESHOLDS.SEVERE.ipm;
    }

    if (ipmDeviation < -threshold) {
      anomalies.push({
        metric: 'IPM',
        deviation: ipmDeviation,
        current: record.ipm,
        average: averages.avg_ipm,
        direction: 'decrease',
        text: `IPM ${ipmDeviation.toFixed(1)}% (${record.ipm.toFixed(2)} vs avg ${averages.avg_ipm.toFixed(2)})`
      });
    }
  }

  if (anomalies.length === 0) return null;

  // –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º impact score
  const impactScore = calculateImpactScore(record, anomalies, volumeTier);

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º severity –Ω–∞ –æ—Å–Ω–æ–≤–µ impact score
  let severity;
  if (impactScore >= 150) {
    severity = 'CRITICAL';
  } else if (impactScore >= 100) {
    severity = 'HIGH';
  } else if (impactScore >= 60) {
    severity = 'MEDIUM';
  } else if (impactScore >= 30) {
    severity = 'LOW';
  } else {
    severity = 'INFO';
  }

  return {
    severity: severity,
    impactScore: impactScore,
    volumeTier: volumeTier,
    anomalies: anomalies,
    details: anomalies.map(a => a.text).join(' | ')
  };
}

// ============================================
// –û–ë–†–ê–ë–û–¢–ö–ê –ó–ê–ü–ò–°–ï–ô –ò –§–ò–õ–¨–¢–†–ê–¶–ò–Ø
// ============================================

const detectedAnomalies = [];

allRecords.forEach(record => {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ –∞–ª–µ—Ä—Ç –µ—â–µ –Ω–µ –±—ã–ª –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω
  const notSent = !record.alert_sent || record.alert_sent === '' || record.alert_sent === 'FALSE' || record.alert_sent === false;
  if (!notSent) return;

  // –ü–æ–ª—É—á–∞–µ–º —Å—Ä–µ–¥–Ω–∏–µ –∑–Ω–∞—á–µ–Ω–∏—è –¥–ª—è —ç—Ç–æ–π –∫–∞–º–ø–∞–Ω–∏–∏
  const averages = campaignAverages[record.campaign_id];
  if (!averages) return;

  // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∞–Ω–æ–º–∞–ª–∏–∏
  const anomaly = detectAnomalies(record, averages);

  if (anomaly) {
    // –§–∏–ª—å—Ç—Ä—É–µ–º INFO —É—Ä–æ–≤–µ–Ω—å - –Ω–µ –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –∞–ª–µ—Ä—Ç—ã
    if (anomaly.severity === 'INFO') return;

    detectedAnomalies.push({
      ...record,
      anomaly_severity: anomaly.severity,
      anomaly_impact_score: anomaly.impactScore,
      anomaly_volume_tier: anomaly.volumeTier,
      anomaly_details: anomaly.details,
      anomaly_count: anomaly.anomalies.length
    });
  }
});

// –£–¥–∞–ª—è–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã
const uniqueAnomalies = {};
detectedAnomalies.forEach(record => {
  if (!uniqueAnomalies[record.unique_key]) {
    uniqueAnomalies[record.unique_key] = record;
  }
});

const anomaliesToSend = Object.values(uniqueAnomalies);

// –ï—Å–ª–∏ –Ω–µ—Ç –Ω–æ–≤—ã—Ö –∞–Ω–æ–º–∞–ª–∏–π
if (anomaliesToSend.length === 0) {
  return [{
    json: {
      action: "skip",
      message: "No new anomalies to send"
    }
  }];
}

// –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ impact score (—Å–∞–º—ã–µ –≤–∞–∂–Ω—ã–µ –ø–µ—Ä–≤—ã–º–∏)
anomaliesToSend.sort((a, b) => b.anomaly_impact_score - a.anomaly_impact_score);

// –ì—Ä—É–ø–ø–∏—Ä—É–µ–º –ø–æ severity
const criticalAlerts = anomaliesToSend.filter(r => r.anomaly_severity === 'CRITICAL');
const highAlerts = anomaliesToSend.filter(r => r.anomaly_severity === 'HIGH');
const mediumAlerts = anomaliesToSend.filter(r => r.anomaly_severity === 'MEDIUM');
const lowAlerts = anomaliesToSend.filter(r => r.anomaly_severity === 'LOW');

// ============================================
// –§–û–†–ú–ò–†–û–í–ê–ù–ò–ï –°–û–û–ë–©–ï–ù–ò–Ø –î–õ–Ø SLACK
